        //map provides new array
        return(
            <div>
            <section className="Posts">
                {posts}
            </section>
            <section>
                <FullPost id={this.state.selectedPostId}/>
            </section>
            <section>
                <NewPost />
            </section>
        </div>
        );
    }
}


export default Blog;
