import React, { Component } from 'react';
import axios from 'axios';

import './FullPost.css';

class FullPost extends Component {
    state = {
        loadedPost: null,
    }
    componentDidUpdate(){
        if(this.props.id){
            axios.get('https://jsonplaceholder.typicode.com/posts/'+this.props.id)
            .then(resposne => {
                // console.log(resposne)
                this.setState({loadedPost: resposne.data})
            });
        }
    }

    render () {
        let post = <p style={{textAlign:'center'}}>Please select a Post!</p>;

        //NULL TITLE ERROR KINA AYO VANE... componentDidUpdate() vanda aagi nai 
        //yo tala ko if wala statement run vayo jun ma {this.state.loadedPost.title}, loadedPost
        if(this.props.id){
            post = (
                <div className="FullPost">
                    <h1>{this.state.loadedPost.title}</h1>
                    <p>l{this.state.loadedPost.content}</p>
                    <div className="Edit">
                        <button className="Delete">Delete</button>
                    </div>
                </div>  
            );
        }     
        return post;
    }
}

export default FullPost;
